import React, {useState} from "react";
import clsx from "clsx";
import "../Form.css";
import "../../Typography/Typography.css"

export default function Input(props) {

    const {className, ...attrs} = props;
    const componentBaseClass = "input";
    const hasError = props.hasError ? "has-error" : "";
    const classes = clsx(componentBaseClass, hasError, className);

    return <input type={props.type} name={props.name} className={classes} placeholder={props.plceholder} value={props.value} onChange={props.onChange} disabled={props.disabled} />
}
