import React, {useState} from "react";
import clsx from "clsx";
import "./Form.css";
import "../Typography/Typography.css";
import {SmallBodyText} from "../Typography/Typography.js";

export default function TextArea(props) {

    const {className, ...attrs} = props;
    const componentBaseClass = "input";
    const hasError = props.hasError ? "has-error" : "";
    const classes = clsx(componentBaseClass, "textarea", hasError, className);

    return (
        <div className="form-field">
            {props.label && <label class="text--label">{props.label}</label>}
            <textarea type={props.type} name={props.name} className={classes} placeholder={props.placeholder} value={props.value} onChange={props.onChange} disabled={props.disabled}>
                {props.children}
            </textarea>
            {props.hasError && <SmallBodyText>This is an error</SmallBodyText>}
        </div>
        )
}
