import React from 'react';
import clsx from "clsx";
import Container from '../Container/Container.js';
import "../Typography.css";
import "../Reset.css";
import "./Card.css";

export default function Card(props) {
    const {className, ...attr} = props;

    const componentBaseClass = "card";
    // Setting a class for a property: const propClass = props.propClass ? componentBaseClass + "--" + props.propClass : "";
    // Setting the classes for the component: const classes = clsx(componentBaseClass + " " + propClass, className);

    // Available Types (see CSS): undefined, box, border
    // Available Sizes (see Container CSS): undefined, small, medium, large
    // Available Spacings (see Container CSS): undefined, small, medium, large
    // Available Layout (see Container CSS): undefined, 2, 3, 4, auto

    const type = props.type ? componentBaseClass + "--" + props.type : "";
    const classes = clsx(componentBaseClass + " " + type + " " + size + " " + spacing + " " + layout, className);

return <Container className={classes} type={props.type} size={props.size} spacing={props.spacing} layout={props.layout}>{props.children}</Container>
}
